type Place = {
    /**
     * Place id as defined by the database
     */
    id: number,

    /**
     * Place name as entered when created
     */
    name: string

    /**
     * Description as entered when created
     */
    description: string

    /**
     * Country name of place
     */
    country: string
    /**
     * Country id
     */
    cid: number
}
